import React from 'react'
import { useState, useEffect } from 'react'

function UserSeeProjects() {

    const [projects, setProjects] = useState([])
    const [currentUser, setCurrentUser] = useState([])

    useEffect(() => {
        fetch('/user-homepage')
        .then(response => response.json())
        .then(data => {
            console.log(data)
            setCurrentUser(data)
        })
    }, [])

    useEffect(() => {
        fetch('/projects')
        .then(response => response.json())
        .then(data => { 
            console.log(data)
            setProjects(data.projects)
        })

    }, [])

    const mappedProjects = currentUser?.projects?.map(project => {
        return(
            <div className="project-card" key={project.id}>
                <h2>{project.title}</h2>
                <img className= 'project-image'src={project.image} alt="Project Pic"/>
                <p>{project.description}</p>
                <p>{project.location}</p>
                <p>{project.genre}</p>
            </div>
        )
    })







  return (
    <div>
        <h1>Projects You Want To Work On</h1>
        {mappedProjects}

    </div>
  )
}

export default UserSeeProjects
