import React from 'react'
import { useState, useEffect } from 'react'


function EditOwnerInfo() {

    const [isCamera, setIsCamera] = useState(false)
    const [isLights, setIsLights] = useState(false)
    const [isAudio, setIsAudio] = useState(false)
    const [isEditing, setIsEditing] = useState(false)
    const [isProps, setIsProps] = useState(false)
    const [editedOwner, setEditedOwner] = useState({})

    const handleCamera = (e) => {
        setIsCamera(e.target.checked)
    }

    const handleFileChange = (e) => {
        const file =e.target.files[0]
        console.log(file)
        const formData = new FormData()
        formData.append('profile-pic', file)
        fetch('/owner/edit', {
            method: 'POST',
            body: formData
        })
        .then(response => response.json())
        .then(data => {
            console.log(data)
            setEditedOwner(data)
        })
    }

    const handleEditSubmit = (e) => {
        e.preventDefault()
        fetch('/owner/edit', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
                },
            }
        )
        .then(response => response.json())
        .then(data => {
            console.log(data)
        })
    }





  return (
    <div className='edit-info-page'>
        <h3>Edit Your Info</h3>
        <form className='edit-info-form'>
            <h3>Email</h3>
            <input type='text' placeholder='Email' />
            <h3>Kit Options</h3>
            <div className='kit-option-checkbox'>
                <p>Camera Equipment</p>
                <input type='checkbox' checked={isCamera} onChange={handleCamera} placeholder='Camera Equipment' />
                <br></br>
                <p>Lighting Equipment</p>
                <input type='checkbox' checked={isChecked} onChange={handleCheck} placeholder='Lighting Equipment' />
                <br></br>
                <p>Audio Equipment</p>
                <input type='checkbox' checked={isChecked} onChange={handleCheck} placeholder='Audio Equipment' />
                <p>Editing Software</p>
                <input type='checkbox' checked={isChecked} onChange={handleCheck} placeholder='Editing Software' />
                <br></br>
                <p>Props</p>
                <input type='checkbox' checked={isChecked} onChange={handleCheck} placeholder='Props' />
            </div>
            <h3>Location</h3>
            <input type='text' placeholder='Location' />
            <h3>Experience</h3>
            <input type='integer' placeholder='Experience' />   
            <h3>Bio</h3>
            <input type='text' placeholder='Bio' />
            <h3>Profile Picture</h3>
            <input type='file' id='profile-pic' name='profile-pic' accept='image/*' onChange={handleFileChange}/>
        </form>



    </div>
  )
}

export default EditOwnerInfo
